$(document).ready(function(){var o="rgba(255,255,255,0)",a=['<a href="#top" class="back-to-top"><i class="fa fa-arrow-up" aria-hidden="true"></i></a>'].join("");$("body").append(a),$(".back-to-top").hide(),$(function(){$(window).scroll(function(){$(this).scrollTop()>100?$(".back-to-top").fadeIn():$(".back-to-top").fadeOut()}),$(".back-to-top").click(function(){return $("body,html").animate({scrollTop:0,backgroundColor:o},800),!1})}),$(function(){$(window).scroll(function(){$(this).scrollTop()<50?$("nav").animate({backgroundColor:o},250):$("nav").animate({backgroundColor:"#241F2B"},500)})})});